function get_eco2(e) {
    fetch("/api/eco3.json").then(function (n) {
        n.json().then(function (n) {
            e({
                ...n.mining
            })
        })
    })
}

function format_price(e) {
    return "$" + e.toFixed(2).toString().replace(/\B(?<!\.\d*)(?=(\d{3})+(?!\d))/g, ",")
}

function render_eco2() {
    get_eco2(function (e) {
        var n = document.querySelector(".main-table-eco2");
        for (let i in e) {
            var t = e[i],
                a = document.createElement("tr");
            a.className = "main-table-eco-row";
            var c = document.createElement("td");
            c.className = "main-table-eco-cell", c.innerHTML = t.name;
            var l = document.createElement("td");
            l.className = "main-table-eco-cell", l.innerHTML = format_price(t.cost);
            var o = document.createElement("td");
            o.className = "main-table-eco-cell", o.innerHTML = format_price(t.sellprice);
            var r = document.createElement("td");
            r.className = "main-table-eco-cell", r.innerHTML = t.spawnRate.join(" & "), a.appendChild(c), a.appendChild(l), a.appendChild(o), a.appendChild(r), n.appendChild(a)
        }
    })
}
