function get_eco3(e) {
    fetch("/api/eco.json").then(function (n) {
        n.json().then(function (n) {
            e({
                ...n.farming
            })
        })
    })
}

function format_price(e) {
    return "$" + e.toFixed(2).toString().replace(/\B(?<!\.\d*)(?=(\d{3})+(?!\d))/g, ",")
}

function render_eco3() {
    get_eco3(function (e) {
        var n = document.querySelector(".main-table-eco3");
        for (let l in e) {
            var t = e[l],
                c = document.createElement("tr");
            c.className = "main-table-eco-row";
            var a = document.createElement("td");
            a.className = "main-table-eco-cell", a.innerHTML = t.name;
            var o = document.createElement("td");
            o.className = "main-table-eco-cell", o.innerHTML = format_price(t.cost);
            var r = document.createElement("td");
            r.className = "main-table-eco-cell", r.innerHTML = format_price(t.sellprice), c.appendChild(a), c.appendChild(o), c.appendChild(r), n.appendChild(c)
        }
    })
}